import "slick-carousel/slick/slick.css";
import "slick-carousel/slick/slick-theme.css";
import React, { Component } from "react";
import News from "../NewsComponent/News";
import Slider from "react-slick";

export default class Carousel extends Component {
  render() {
    const settings = {
      dots: true,
      infinite: true,
      //autoplay: true,
      speed: 500,
      slidesToShow: 3,
      slidesToScroll: 3,
      appendDots: (dots) => (
        <div
          style={{
            backgroundColor: "#ddd",

            padding: "10px",
          }}
        >
          <ul style={{ margin: "0px" }}> {dots} </ul>
        </div>
      ),
      customPaging: (i) => (
        <div
          style={{
            width: "10px",
            color: "blue",
            backgroundColor: "#166da1",
          }}
        >
          {i + 1}
        </div>
      ),
    };
    return (
      <div>
        <Slider {...settings}>
          <div>
            <News />
          </div>
          <div>
            <News />
          </div>
          <div>
            <News />
          </div>
          <div>
            <News />
          </div>
          <div>
            <News />
          </div>
          <div>
            <News />
          </div>
          <div>
            <News />
          </div>
          <div>
            <News />
          </div>
          <div>
            <News />
          </div>
        </Slider>
      </div>
    );
  }
}
